import { createStore } from "vuex";
import postsJson from "@/data/posts.json";

export default createStore({
  strict: true,

  state: {
    posts: postsJson
  },

  getters: {
    allPosts: state => state.posts,
    postsByTag: state => tag => state.posts.filter(p => p.tags.includes(tag))
  },

  mutations: {
    IncreaseLikes(state, id) {
      state.posts.forEach(post => {
        if (post.id === id) {
          post.likes += 1;
        }
      });
    },

    ResetLikes(state) {
      state.posts.forEach(post => {
        post.likes = 0;
      });
    }
  },

  actions: {
    increaseLikeAct({ commit }, id) {
      setTimeout(() => {
        commit("IncreaseLikes", id);
      }, 500);
    },

    resetLikesAct({ commit }) {
      setTimeout(() => {
        commit("ResetLikes");
      }, 500);
    }
  }
});
